mapper.select:
select * from view_pstudent where 
         termYear=#{0}
       and type =#{1}	   and stuType=#{2}

	    select * from view_pstudent where 
	             termYear=#{0}
             and stuType=#{3}
	         and type in (#{1},#{2})

select * from view_pstudent where 
         termYear=#{0}
     and    major=#{1}	   and stuType=#{2}

select * from view_pstudent where 
         termYear=#{0}
      and   major=#{1}
       and type =#{2}	   and stuType=#{3}

	    select * from view_pstudent where 
	             termYear=#{0}
	          and major=#{1}
              and stuType=#{4}
	          and type in (#{2},#{3})

select * from view_pstudent where 
         termYear=#{0}
           and   major=#{1}
           and class=#{2}	   and stuType=#{3}

select * from view_pstudent where 
         termYear=#{0}
      and   major=#{1}
      and class=#{2}	
       and  type =#{3}	   and stuType=#{4}

	    select * from view_pstudent where 
	             termYear=#{0}
	               and   major=#{1}
	               and class=#{2}	
                   and stuType=#{5}
	               and type in (#{3},#{4})
