mapper.select:
select * from view_skill where 
         termYear=#{0}
       and skillType=#{1} and stuType=#{2}

	    select * from view_skill where 
	             termYear=#{0}
             and stuType=#{3}
	         and skillType in (#{1},#{2})

select * from view_skill where 
         termYear=#{0}
   and      major=#{1}   and stuType=#{2}

select * from view_skill where 
         termYear=#{0}
      and   major=#{1}
       and skillType=#{2}   and stuType=#{3}

	    select * from view_skill where 
	             termYear=#{0}
	          and   major=#{1}
              and stuType=#{4}
	          and skillType in (#{2},#{3})

	    select * from view_skill   
	             where termYear=#{0}
	        and   major=#{1}	      	   		     
            and stuType=#{5}
	        and skillType in (#{2},#{3},#{4})

select * from view_skill where 
         termYear=#{0}
           and   major=#{1}
           and class=#{2}	  and stuType=#{3}

	    select * from view_skill where 
	             termYear=#{0}
               and   major=#{1}
	            and class=#{2}	      	   		     
		 	 and  skillType=#{3}    and stuType=#{4}

	    select * from view_skill where 
	             termYear=#{0}
	               and   major=#{1}
	            and class=#{2}	      	   		     
                and stuType=#{5}
	            and skillType in (#{3},#{4})

	    select * from view_skill   
	             where termYear=#{0}
	               and   major=#{1}
	            and class=#{2}	      	   		     
                and stuType=#{6}
	            and skillType in (#{3},#{4},#{5})
