mapper.select:
        select DISTINCT * from view_xjzc   
             where termYear=#{0}	      		      	   		     	    		    
	 	         and stuType=#{3}

        select * from view_xjzc   
             where termYear=#{0}	      		      	     	 			    	   		     	    		    
	 	 and ZCyear=#{1}
	 	 and term=#{2}
	 	 and ZCorNot="1"	 and stuType=#{3}

        select * from view_all   
             where termYear=#{0}	 and stuType=#{3}	       		      		   		     	    		    
	 	 and  studentNO not in (select studentNo from view_xjzc where termYear=#{0} 
	 	                                                          and ZCyear=#{1}
	 	                                                          and term=#{2} 
	 	                                                          and ZCorNot="1"
	 	                                                           and stuType=#{3}	 )

	        select * from view_xjzc   
	             where termYear=#{0}
	          and major=#{1}	      		      	   		     	    		    
              and stuType=#{4}

        select * from view_xjzc   
             where termYear=#{0}
               and major=#{1}	      		      	     	 			    	   		     	    		    
	 	 and ZCyear=#{2}
	 	 and term=#{3}
	 	 and ZCorNot="1"		  and stuType=#{4}

        select * from view_all   
             where termYear=#{0}
             and major=#{1}		 and stuType=#{4}	       		      		   		     	    		    
	 	 and  studentNO not in (select studentNo from view_xjzc where termYear=#{0} 
	 	                                                          and major=#{1}
	 	                                                          and ZCyear=#{2}
	 	                                                           and term=#{3}
	 	                                                            and ZCorNot="1"
	 	                                                             and stuType=#{4}	 )

	        select * from view_xjzc   
	             where termYear=#{0}
	             and major=#{1}
	             and class=#{2}	      		      	   		     	    		    
                 and stuType=#{5}

        select * from view_xjzc   
             where termYear=#{0}	      		      	     	 			    	   		     	    		    
	 	      and major=#{1}
             and class=#{2}	      		      	   		     	    		    
	 	 and ZCyear=#{3}
	 	 and term=#{4} 
	 	 and ZCorNot="1"    and stuType=#{5}

        select * from view_all   
             where termYear=#{0}
               and major=#{1}
             and class=#{2}	  and stuType=#{5}	 	      		      		   		     	    		    
	 	and  studentNO not in (select studentNo from view_xjzc where termYear=#{0} 
	 	                                                          and major=#{1}
	 	                                                          and class=#{2}
	 	                                                          and ZCyear=#{3}
	 	                                                          and term=#{4}
	 	                                                          and ZCorNot="1"
	 	                                                           and stuType=#{5}	 )
